import React, { Component } from "react";

class Login extends Component {
    render(){
        function showHideBox(){
            document.getElementById('box').style.display = "block";
            document.querySelector('input[name="radio"]:checked').checked = false;
            document.getElementById('cbx').onclick = function() {
                var checkboxes = document.getElementsByName('vehicle');
                for (var checkbox of checkboxes) {
                    checkbox.checked = true;
                }
            }
          }
        return(
            <section className="h-100 gradient-form">
                <div className="container py-5 h-100" style={{align: "center"}}>
                    <div className="row d-flex justify-content-center align-items-center h-100">
                        <div className="col-xl-10">
                            <div className="card rounded-3 text-black">
                                <div className="card-body p-md-5 mx-md-4">
                                    <div className="text-center">
                                        <img src={this.props.logo}
                                            style={{ width: "185px"}} alt="logo"/>
                                        <h4 className="mt-1 mb-5 pb-1">We are Zamira Kebab</h4>
                                    </div>

                                    <form>
                                        <p>Please login to your account</p>

                                        <div className="form-floating mb-4">
                                            <input type="email" id="form2Example11" className="form-control" placeholder="Phone number or email address"/>
                                            <label className="form-label" htmlFor="form2Example11">Email address</label>
                                        </div>

                                        <div className="form-floating mb-4">
                                            <input type="password" id="form2Example22" className="form-control" placeholder="Password"/>
                                            <label className="form-label" htmlFor="form2Example22">Password</label>
                                        </div>

                                        <div className="text-center pt-1 mb-5 pb-1">
                                            <button className="btn btn-outline-primary" type="button" onClick={()=>{ showHideBox(); }}>Login</button>
                                            <br></br>
                                            <a className="text-muted" href="#!">Forgot password?</a>
                                        </div>

                                        <div className="d-flex align-items-center justify-content-center pb-4">
                                            <p className="mb-0 me-2">Don't have an account?</p>
                                            <button type="button" className="btn btn-outline-danger">Create new</button>
                                        </div>

                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        );
    }
}

export default Login
