import React, { Component } from "react";
import './popupStyle.css';

class Popup extends Component {
  render(){
    function hideBox(){
      document.getElementById('box').style.display = "none";
    }
    return(
      <>
      <div id="box" className="container">
        <p><img src={this.props.logo1}/> Ingrediente 1</p> <input type={"checkbox"}/> <br/>
        <p><img src={this.props.logo1}/> Ingrediente 2</p> <input type={"checkbox"}/> <br/>
        <p><img src={this.props.logo1}/> Ingrediente 3</p> <input type={"checkbox"}/> <br/>
        <p><img src={this.props.logo1}/> Ingrediente 4</p> <input type={"checkbox"}/> <br/>
        <p><img src={this.props.logo1}/> Ingrediente 5</p> <input type={"checkbox"}/> <br/>

        <hr/>

        <p><img src={this.props} /> No Bibita</p> <input name="radio" type={"radio"}/> <br/>
        <p>Bibita 1</p> <input name="radio" type={"radio"}/> <br/>
        <p>Bibita 2</p> <input name="radio" type={"radio"}/> <br/>
        <p>Bibita 3</p> <input name="radio" type={"radio"}/> <br/>
        <p>Bibita 4</p> <input name="radio" type={"radio"}/> <br/>
        <p>Bibita 5</p> <input name="radio" type={"radio"}/> <br/>

        <hr/>

        <button className="btn btn-outline-danger" onClick={ ()=>{ hideBox(); } }>Chiudi</button>
        <button className="btn btn-outline-success">Aggiungi al carrello</button>

      </div>
      </>
    );
  }
}

export default Popup;
